drop table if exists wonks;
create table wonks
(
	wonk_id integer,
	name text,
	bio_url text,
	blog_url text,
	web_url text,
	twitter_url text,
	twitter_id integer,
	also_found_in text,
	email text,
	phone text
);
drop table if exists wonk_topics;
create table wonk_topics
(
	wonk_id integer,
	topic text,
	reject_flag integer
);

drop table if exists wonk_news_items;
create table wonk_news_items
(
	wonk_id integer,
	title text,
	news_date date,
	news_type text,
	news_teaser text,
	news_url text
);
	
